// Code-–Ω–æ–¥–∞: items ‚Äî —ç—Ç–æ –≤—Ö–æ–¥–Ω–æ–π –º–∞—Å—Å–∏–≤
return items.map(item => {
  const prompt   = item.json.prompt;
  const mimeType = item.json.mimeType;
  const fileUri  = item.json.gemini_file_url;

  // –¥–æ–±–∞–≤–ª—è–µ–º –≤ json –Ω–æ–≤–æ–µ –ø–æ–ª–µ body
  item.json.body = {
    contents: [
      {
        parts: [
          { text: prompt },
          {
            file_data: {
              mime_type: mimeType,
              file_uri:  fileUri,
            },
          },
        ],
      },
    ],
  };

  return item;
});

// –ü—Ä–æ—Å—Ç–æ–π –ø–∞—Ä—Å–µ—Ä –¥–ª—è –∏–∑–≤–ª–µ—á–µ–Ω–∏—è JSON –∏–∑ Gemini –æ—Ç–≤–µ—Ç–∞
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    // –ò–∑–≤–ª–µ–∫–∞–µ–º text –∏–∑ Gemini —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
    const geminiText = item.json.candidates[0].content.parts[0].text;

    console.log('Raw Gemini text:', geminiText);

    // –£–±–∏—Ä–∞–µ–º –≤—Å–µ markdown –±–ª–æ–∫–∏ –∏ –ª–∏—à–Ω–∏–µ —Å–∏–º–≤–æ–ª—ã
    let cleanJson = geminiText
      .replace(/```json/g, '')  // —É–±–∏—Ä–∞–µ–º ```json
      .replace(/```/g, '')      // —É–±–∏—Ä–∞–µ–º ```
      .replace(/^\s*[\r\n]/gm, '') // —É–±–∏—Ä–∞–µ–º –ø—É—Å—Ç—ã–µ —Å—Ç—Ä–æ–∫–∏
      .trim();                  // —É–±–∏—Ä–∞–µ–º –ø—Ä–æ–±–µ–ª—ã –ø–æ –∫—Ä–∞—è–º

    console.log('Cleaned JSON:', cleanJson);

    // –ü–∞—Ä—Å–∏–º JSON
    const parsed = JSON.parse(cleanJson);

    console.log('Parsed result:', parsed);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
    results.push({
      json: {
        ...item.json,
        lipsync_result: parsed,
        // –£–¥–æ–±–Ω—ã–µ –ø–æ–ª—è –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
        is_suitable: parsed.suitable_for_lipsync,
        has_defect: !parsed.suitable_for_lipsync,
        defect_type: parsed.critical_defect,
        defect_info: parsed.defect_description
      },
      binary: item.binary || {}
    });

  } catch (error) {
    console.error('Parse error:', error.message);

    results.push({
      json: {
        ...item.json,
        error: error.message,
        lipsync_result: {
          suitable_for_lipsync: false,
          critical_defect: "–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞",
          defect_description: error.message
        },
        is_suitable: false,
        has_defect: true
      },
      binary: item.binary || {}
    });
  }
}

return results;

// –ü–æ–ª—É—á–∞–µ–º e-mail –∏–∑ –≤—Ö–æ–¥—è—â–∏—Ö –¥–∞–Ω–Ω—ã—Ö
const email = $('valid main?').first().json.Mail

// –û–±—Ä–µ–∑–∞–µ–º –≤—Å–µ –ø–æ—Å–ª–µ –∏ –≤–∫–ª—é—á–∞—è @
const nickname = email.split('@')[0];

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
return [
  {
    json: {
      nickname,
    }
  }
];
